sprites.onOverlap(SpriteKind.musharoom, SpriteKind.Projectile, function (sprite, otherSprite) {
    sprite.destroy()
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.musharoom, function (sprite, otherSprite) {
    if (sprite.y < otherSprite.top) {
        otherSprite.destroy(effects.spray, 500)
    } else {
        otherSprite.destroy()
        marioDie()
    }
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.LevelUP, function (sprite, otherSprite) {
    otherSprite.destroy()
    marioImage = 2
})
controller.A.onEvent(ControllerButtonEvent.Pressed, function () {
    if (superMario.vy == 0) {
        superMario.vy = -115
    }
})
scene.onHitWall(SpriteKind.turtle, function (sprite, location) {
    if (sprite.isHittingTile(CollisionDirection.Right)) {
        if (!(turtleREACTION == 3)) {
            turtleREACTION = 2
        }
        sprite.vx = TurtleVXspeed - TurtleVXspeed * 2
    } else if (sprite.isHittingTile(CollisionDirection.Left)) {
        if (!(turtleREACTION == 3)) {
            turtleREACTION = 1
        }
        sprite.vx = TurtleVXspeed
    }
})
