sprites.onOverlap(SpriteKind.Player, SpriteKind.turtle, function (sprite, otherSprite) {
    if (sprite.y < otherSprite.top) {
        superMario.vy = -115
        turtleREACTION = 3
        TurtleVXspeed = 140
        otherSprite.vx = TurtleVXspeed
    } else {
        marioDie()
        otherSprite.destroy()
    }
})
