sprites.onOverlap(SpriteKind.Player, SpriteKind.musharoom, function (sprite, otherSprite) {
    if (sprite.y < otherSprite.top) {
        otherSprite.destroy(effects.spray, 500)
    } else {
        otherSprite.destroy()
        marioDie()
    }
})
scene.onOverlapTile(SpriteKind.Player, myTiles.tile7, function (sprite, location) {
    InitLevel()
})
scene.onHitWall(SpriteKind.musharoom, function (sprite) {
    if (sprite.isHittingTile(CollisionDirection.Right)) {
        sprite.vx = -50
    } else if (sprite.isHittingTile(CollisionDirection.Left)) {
        sprite.vx = 50
    }
})
scene.onHitWall(SpriteKind.turtle, function (sprite) {
    if (sprite.isHittingTile(CollisionDirection.Right)) {
        if (!(turtleREACTION == 3)) {
            turtleREACTION = 2
        }
        sprite.vx = -50
    } else if (sprite.isHittingTile(CollisionDirection.Left)) {
        if (!(turtleREACTION == 3)) {
            turtleREACTION = 1
        }
        sprite.vx = 50
    }
})
